import java.sql.*;/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * * @author mano */public class Movies {    Movies(){};    public void create(int idMovie,String title, String releaseDate, int duration, int score)throws SQLException{        Connection connection = Database.getConnection();                try(PreparedStatement preparedStatement = connection.prepareStatement(                "INSERT INTO movies(id_movie,title,release_date,duration,score) values(?,?,?,?,?) ")){                                 preparedStatement.setInt(1,idMovie);                     preparedStatement.setString(2,title);                     preparedStatement.setString(3,releaseDate);                     preparedStatement.setInt(4,duration);                     preparedStatement.setInt(5,score);                     preparedStatement.executeUpdate();                     preparedStatement.close();                     connection.commit();        }    }           public Integer findByName(String name) throws SQLException {                Connection conn = Database.getConnection();                String sql="SELECT id FROM movies WHERE name='"+name+"'";                Statement statement=conn.prepareStatement(sql);                ResultSet resultSet=statement.executeQuery(sql);                if(resultSet.next()) {return resultSet.getInt(1);}                return null;        }}